describe StockInBillsController do
  login_admin
  let(:warehouse) { FactoryGirl.create(:seller,:account_id => current_account.id) }
  let(:stock_in_bill) {FactoryGirl.create(:stock_in_bill,:account_id => current_account.id,:seller_id => warehouse.id)}
  let(:sku) { create(:sku,:account_id => current_account.id) }
  before(:each) { @stock_in_bill = stock_in_bill }
  # This should return the minimal set of attributes required to create a valid
