  def valid_attributes
    {stock_type: "IOT", bill_products_attributes: { "0" => {sku_id: sku.id, number: 10, price: 10.0, real_number: 1, total_price: 100.0 }}}
  end

  # This should return the minimal set of values that should be in the session
