  describe "GET index" do
    it "assigns all stock_in_bills as @stock_in_bills" do
      get :index,{:warehouse_id => warehouse}, valid_session
      assigns(:bills).should eq([stock_in_bill])
    end
  end

  describe "GET show" do
    it "assigns the requested stock_in_bill as @stock_in_bill" do
      get :show, {:warehouse_id => warehouse,:id => stock_in_bill.to_param}, valid_session
      assigns(:bill).should eq(stock_in_bill)
    end
  end

  describe "GET new" do
