      warehouse.save
      get :new, {:warehouse_id => warehouse}, valid_session
      assigns(:bill).should be_a_new(StockInBill)
    end
  end

  describe "GET edit" do
    it "assigns the requested stock_in_bill as @stock_in_bill" do
      get :edit, {:warehouse_id => warehouse,:id => stock_in_bill.to_param}, valid_session
      assigns(:bill).should eq(stock_in_bill)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new StockInBill" do
        expect {
          post :create, {:warehouse_id => warehouse,:stock_in_bill => valid_attributes}, valid_session
        }.to change(StockInBill, :count).by(1)
      end

      it "assigns a newly created stock_in_bill as @stock_in_bill" do
        post :create, {:warehouse_id => warehouse,:stock_in_bill => valid_attributes}, valid_session
        assigns(:bill).should be_a(StockInBill)
        assigns(:bill).should be_persisted
