      it "redirects to the created stock_in_bill" do
        post :create, {:warehouse_id => warehouse,:stock_in_bill => valid_attributes}, valid_session
        response.should redirect_to(warehouse_stock_in_bill_path(warehouse,StockInBill.last))
      end
    end

